options: {
  languages: [
    {
      name: "en",
      filterNodes: (node) =>
        node.frontmatter !== undefined &&
        node.fileAbsolutePath &&
        node.fileAbsolutePath.match(
          /\/en\/(?!header.mdx|index.mdx|404.mdx|.js|.json)/
        ) !== null,
    },
  ];
}

